    def horaSistema(self):
        self.hora = datetime.today().strftime('%H:%M:%S')
        print(type(self.hora))
        self.hora = list(self.hora)
        self.hora = self.hora[:2]
        print(self.hora)
        self.hora = ''.join(self.hora)
        self.hora = int(self.hora)
        if self.hora >=6 and self.hora<=11:
            self.label_29.setText(str("BOM DIA"))
        if self.hora >=12 and self.hora<=17:
            self.label_29.setText("BOA TARDE")
        if self.hora >= 18 and self.hora <= 00:
            self.label_29.setText("BOA NOITE")
        if self.hora >= 00 and self.hora <= 5:
            self.label_29.setText(str("BOA MADRUGADA"))
        print(self.hora)

