    def verificaEDV(self):
        self.horaBD = datetime.today().strftime('%Y-%m-%d %H:%M:%S')

        self.edv = self.txtEDV.text()

        self.edvInt = int(self.edv)
        print(self.edvInt)
        self.cursor.execute("SELECT edv FROM colaborador edv={}".format(edv))
        myresult = self.cursor.fetchall()
        if myresult != '':
            print("dados nao encontrados!")

        myresult = str(myresult)
        print(myresult)
        print('hora: ',self.horaBD)
        if myresult != '[]':
            nomePC=str(self.txtUser.text())

            self.cursor.execute("SELECT codPC FROM desktop where nomePC='{}'".format(nomePC))
            myresult2 = self.cursor.fetchone()
            myresult2 = int(myresult2[0])
            print(myresult2)

            self.widget.setEnabled(True)
            self.txtEDV.setEnabled(True)
            self.btnOK.setEnabled(False)
            self.hora = str(self.hora)

            self.cursor.execute("INSERT INTO verificacao VALUES ({},{},'2','','2','','2','','2','2','','2','','2','','2','','{}')".format(self.edvInt,myresult2,self.horaBD))

            self.con.commit()
            print(self.cursor.rowcount, "record inserted.")

        else:
            msg = QMessageBox()
            msg.setIcon(QMessageBox.Information)
            msg.setText("EDV não encontrado!")
            msg.exec()
            self.txtEDV.setText('')

